function uploadFile (accessToken, filePath) {
  var dbox = require('dbox');
	var APP_KEY = '7a870efg25be689',
      APP_SECRET = '0ly99wm2hgau5qq';

  var app = dbox.app({
      'app_key': APP_KEY,
      'app_secret': APP_SECRET
  });

  var client = app.client(accessToken)
  client.put('hello.txt', "here is some text", function(status, reply){
	  console.log(reply);
	});
	return client.shares(filePath, {}, function(status, reply){
	  return reply['url'];
	});
}

module.exports = uploadFile;